import React, { useEffect, useRef } from "react";

const Ambulance = () => {
    const mapRef = useRef(null);
    
    const victimLocations = [
        { lat: 23.8103, lng: 90.4125 }, // Dhaka
        { lat: 24.3636, lng: 88.6241 }  // Rajshahi
    ];

    const volunteerLocations = [
        { lat: 22.3569, lng: 91.7832 }, // Chittagong
        { lat: 23.2513, lng: 89.5669 }  // Khulna
    ];

    useEffect(() => {
        const initMap = () => {
            navigator.geolocation.getCurrentPosition(position => {
                const { latitude, longitude } = position.coords;

                const map = new window.google.maps.Map(mapRef.current, {
                    zoom: 7,
                    center: { lat: latitude, lng: longitude }
                });

                // Add red markers for victims
                victimLocations.forEach(location => {
                    new window.google.maps.Marker({
                        position: location,
                        map: map,
                        icon: {
                            path: window.google.maps.SymbolPath.CIRCLE,
                            fillColor: "red",
                            fillOpacity: 1,
                            strokeColor: "white",
                            strokeWeight: 2,
                            scale: 10
                        },
                        title: "Victim Location"
                    });
                });

                // Add green markers for volunteers
                volunteerLocations.forEach(location => {
                    new window.google.maps.Marker({
                        position: location,
                        map: map,
                        icon: {
                            path: window.google.maps.SymbolPath.CIRCLE,
                            fillColor: "green",
                            fillOpacity: 1,
                            strokeColor: "white",
                            strokeWeight: 2,
                            scale: 10
                        },
                        title: "Volunteer Location"
                    });
                });

                // OpenWeatherMap Tile Overlay
                const openWeatherMapLayer = new window.google.maps.ImageMapType({
                    getTileUrl: (coord, zoom) =>
                        `https://tile.openweathermap.org/map/precipitation_new/${zoom}/${coord.x}/${coord.y}.png?appid=5510e55cbbf0fe0a5bdb54fac87c8cd4`,
                    tileSize: new window.google.maps.Size(256, 256),
                    opacity: 0.6,
                    name: "OpenWeatherMap Precipitation"
                });

                // Add OpenWeatherMap tiles to the map
                map.overlayMapTypes.insertAt(0, openWeatherMapLayer);
            });
        };

        if (window.google) {
            initMap();
        } else {
            const script = document.createElement("script");
            script.src = "https://maps.gomaps.pro/maps/api/js?key=AlzaSy6A8QOu_N_UDfp3vosT8qwPTxbQ3hH2cpK";
            script.async = true;
            script.onload = initMap;
            document.head.appendChild(script);
        }
    }, []);

    return (
        <div ref={mapRef} style={{ height: "100vh", width: "100%" }} />
    );
};

export default Ambulance;
