import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import '../style/Home.css';

// Other code remains unchanged...

export default function Home() {
    const [isMobile, setIsMobile] = useState(false);

    useEffect(() => {
        const handleResize = () => {
            setIsMobile(window.innerWidth <= 768);
        };
        handleResize();
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const services = [
        { img: require('../Images/service1.jpg'), caption: 'Top Specialists', line1: 'Schedule your healthcare consultations with ease, from the comfort of your home', line2: 'Instant, convenient, and always available' },
        { img: require('../Images/service2.jpg'), caption: 'Quick Emergency Support', line1: 'In critical moments, trust us to be there', line2: 'Fast, reliable ambulance services when every second counts' },
        { img: require('../Images/service3.jpg'), caption: 'Dependable Pharmacy Service', line1: 'Order medicines online and get them delivered to your door', line2: 'Trusted, timely, and convenient' },
        { img: require('../Images/service4.jpg'), caption: 'Premier Healthcare Facilities', line1: 'Discover trusted hospitals in your area', line2: 'Get the care you need without the hassleâ€”your health is our mission' }
    ];

    const testimonials = [
        {
            name: "Sarah Williams",
            photo: require('../Images/user1.jpg'),
            feedback: "Vital Aid's online appointment service made it so easy to get expert advice without leaving my home. Highly recommended!"
        },
        {
            name: "John Doe",
            photo: require('../Images/user2.jpg'),
            feedback: "The emergency support was incredibly fast and professional. I felt safe knowing help was just a call away."
        },
        {
            name: "Emily Smith",
            photo: require('../Images/user3.jpg'),
            feedback: "Fantastic pharmacy service! I ordered my medicines online, and they were delivered the same day."
        }
    ];

    const PrevArrow = ({ onClick }) => (
        <div className="slider-arrow slider-arrow-prev" onClick={onClick}>
            &#10094;
        </div>
    );

    const NextArrow = ({ onClick }) => (
        <div className="slider-arrow slider-arrow-next" onClick={onClick}>
            &#10095;
        </div>
    );

    const settings = {
        dots: true,
        infinite: true,
        speed: 1000,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        draggable: isMobile,
        arrows: !isMobile,
        prevArrow: !isMobile ? <PrevArrow /> : null,
        nextArrow: !isMobile ? <NextArrow /> : null
    };

    return (
        <div className="rout-container">
            <div className="home-container">
                <div className="hero-content">
                    <h4>Welcome to <span className="highlight highlight-name">Vital Aid</span></h4>
                    <p>WE ARE <br /> <span className="highlight">ALWAYS</span><br />WITH YOU</p>
                    <Link to="/appoinment" className="appointment-btn">
                        Book Appointment
                    </Link>
                </div>

                <div className="slider-container">
                    <h2 className='service-caption'>Explore Our Range of Services</h2>
                    <Slider {...settings}>
                        {services.map((service, index) => (
                            <div key={index} className="slider-item">
                                <div
                                    className="service-card"
                                    style={{ backgroundImage: `url(${service.img})` }}
                                >
                                    <div className="service-card-content">
                                        <h2 className='service-card-heading'>{service.caption}</h2>
                                        <h4 className='service-card-msg'>{service.line1}<br/>{service.line2}</h4>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
                
                {/* Testimonials Section */}
                <div className="testimonials-section">
                    <h2 className='testimonials-caption'>What Our Clients Say</h2>
                    <Slider {...settings}>
                        {testimonials.map((testimonial, index) => (
                            <div key={index} className="testimonial-item">
                                <div className="testimonial-content">
                                    <img src={testimonial.photo} alt={`${testimonial.name}`} className="testimonial-photo" />
                                    <p className="testimonial-feedback">"{testimonial.feedback}"</p>
                                    <h4 className="testimonial-name">- {testimonial.name}</h4>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
                
            </div>
        </div>
    );
}
