import React, { useState } from 'react';
import '../style/Doctors.css';

export default function Doctors() {

    const [openDropdown, setOpenDropdown] = useState(null);
    const [selectedValues, setSelectedValues] = useState(Array(5).fill(''));

    const toggleDropdown = (index) => {
        setOpenDropdown(openDropdown === index ? null : index);
    };

    const handleSelect = (index, value) => {
        const updatedValues = [...selectedValues];
        updatedValues[index] = value;
        setSelectedValues(updatedValues);
        setOpenDropdown(null);
    };

    const dropdownOptions = [
        ['Option 1A', 'Option 1B', 'Option 1C'],
        ['Option 2A', 'Option 2B', 'Option 2C'],
        ['Option 3A', 'Option 3B', 'Option 3C'],
        ['Option 4A', 'Option 4B', 'Option 4C'],
        ['Option 5A', 'Option 5B', 'Option 5C'],
    ];

    return (
        <div className="rout-container">
            <div className="doctor-container">

                <div className="filter-container">
                    {dropdownOptions.map((options, index) => (
                        <div key={index} className="filter">
                            <button onClick={() => toggleDropdown(index)} className="filter-toggle">
                                {selectedValues[index] || `Filter ${index + 1}`}
                            </button>
                            <div className={`filter-menu ${openDropdown === index ? 'show' : ''}`}>
                                {options.map((option, i) => (
                                    <div
                                        key={i}
                                        className="filter-item"
                                        onClick={() => handleSelect(index, option)}
                                    >
                                        {option}
                                    </div>
                                ))}
                            </div>
                        </div>
                    ))}
                </div>

            </div>
        </div>
    )
}
