import React, { useState } from 'react';
import '../style/Doctors.css';

export default function Doctors() {

    const [selectedValues, setSelectedValues] = useState(Array(5).fill(''));

    const handleSelectChange = (index, event) => {
        const updatedValues = [...selectedValues];
        updatedValues[index] = event.target.value;
        setSelectedValues(updatedValues);
    };

    const filters = [
        { name: 'Location', options: ['Dhaka', 'Chittagong', 'Rajshahi', 'Khulna', 'Barishal', 'Sylhet', 'Rangpur', 'Mymensingh '] },
        { name: 'Field', options: ['Neurology', 'Cardiology', 'Dermatology', 'Endocrinology', 'Gastroenterology', 'Hematology', 'Gynecology', 'Oncology', 'Ophthalmology', 'ENT', 'Pediatrics', 'Psychiatry'] },
        { name: 'Hospital', options: ['Option 3A', 'Option 3B', 'Option 3C'] },
        { name: 'consultation Fee', options: ['Option 4A', 'Option 4B', 'Option 4C'] },
        { name: 'Availability', options: ['Option 5A', 'Option 5B', 'Option 5C'] },
    ];

    return (
        <div className="rout-container">
            <div className="doctor-container">

                <div className="filter-container">
                    {filters.map((filter, index) => (
                        <div key={index} className="filter">
                            <label>{filter.name}</label>
                            <select
                                value={selectedValues[index]}
                                onChange={(event) => handleSelectChange(index, event)}
                                className="filter-select"
                            >
                                <option value="">All</option>
                                {filter.options.map((option, i) => (
                                    <option key={i} value={option}>
                                        {option}
                                    </option>
                                ))}
                            </select>
                        </div>
                    ))}
                </div>

            </div>
        </div>
    )
}
