import React from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import '../style/Home.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';

export default function Home() {
    const services = [
        { img: require('../Images/service1.jpg'), name: 'Service 1' },
        { img: require('../Images/service2.jpg'), name: 'Service 2' },
        { img: require('../Images/service3.jpg'), name: 'Service 3' }
    ];

    // Custom Next Arrow
    const NextArrow = (props) => {
        const { onClick } = props;
        return (
            <div className="slick-arrow slick-next" onClick={onClick}>
                <FontAwesomeIcon icon={faChevronRight} />
            </div>
        );
    };

    // Custom Previous Arrow
    const PrevArrow = (props) => {
        const { onClick } = props;
        return (
            <div className="slick-arrow slick-prev" onClick={onClick}>
                <FontAwesomeIcon icon={faChevronLeft} />
            </div>
        );
    };

    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000, 
        draggable: true,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />
    };

    return (
        <div className="rout-container">

            <div className="home-container">

                <div className="hero-content">
                    <h4>Welcome to <span className="highlight">Vital Aid</span></h4>
                    <p>WE ARE <br /> <span className="highlight">ALWAYS</span><br />WITH YOU</p>
                    <Link to="/appoinment" className="appointment-btn">
                        Book Appointment
                    </Link>
                </div>

                <div className="slider-container">
                    <h2 className='service-caption'>Explore Our Range of Services</h2>
                    <Slider {...settings}>
                        {services.map((service, index) => (
                            <div key={index} className="slider-item">
                                <div
                                    className="card"
                                    style={{ backgroundImage: `url(${service.img})` }}
                                >
                                    <div className="card-content">
                                        <h2>{service.name}</h2>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>

            </div>

        </div>
    );
}
