import React from 'react';
import { Link } from 'react-router-dom';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import '../style/Home.css';

export default function Home() {
    const services = [
        { img: require('../Images/service1.jpg'), caption: 'Top Specialists', msg: 'Schedule your healthcare consultations with ease, from the comfort of your home. Instant, convenient, and always available'},
        { img: require('../Images/service2.jpg'), caption: 'Quick Emergency Support', msg: 'In critical moments, trust us to be there. Fast, reliable ambulance services when every second counts'},
        { img: require('../Images/service3.jpg'), caption: 'Dependable Pharmacy Service', msg: 'Order medicines online and get them delivered to your door. Trusted, timely, and convenient'},
        { img: require('../Images/service4.jpg'), caption: 'Premier Healthcare Facilities', msg: 'Discover trusted hospitals in your area. Get the care you need without the hassle—your health is our mission'}
    ];

    const settings = {
        dots: true,
        infinite: true,
        speed: 1000,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000, // Adjust speed as needed
        draggable: true
    };

    return (
        <div className="rout-container">

            <div className="home-container">

                <div className="hero-content">
                    <h4>Welcome to <span className="highlight highlight-name">Vital Aid</span></h4>
                    <p>WE ARE <br /> <span className="highlight">ALWAYS</span><br />WITH YOU</p>
                    <Link to="/appoinment" className="appointment-btn">
                        Book Appointment
                    </Link>
                </div>

                <div className="slider-container">
                    <h2 className='service-caption'>Explore Our Range of Services</h2>
                    <Slider {...settings}>
                        {services.map((service, index) => (
                            <div key={index} className="slider-item">
                                <div
                                    className="card"
                                    style={{ backgroundImage: `url(${service.img})` }}
                                >
                                    <div className="card-content">
                                        <h2>{service.caption}</h2>
                                        <p>{service.msg}</p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>

            </div>

        </div>
    );
}
